import React from 'react';

export default function TodoManageList(props) {
    //console.log(props);
    var handleChange = function(event) {
        debugger;
        this.currItem = {};
        this.currItem = {value: event.target.value};
        console.log("currItem",this.currItem);
        debugger;
    }
    return (
        <div>
            <button onClick={e => props.pushNewItem(this.currItem)}>Add</button>
            <input type="text" onChange={handleChange}></input>
        </div>
    );
}
